/// status : 1
/// msg : ""

class JudgementModel {
  JudgementModel({
      this.judgmentDetails, 
      this.status, 
      this.msg,});

  JudgementModel.fromJson(dynamic json) {
    if(json['judgment_details'] == null || json['judgment_details'] == ""){
      print("No Judgment Found");
    }
    else {
      judgmentDetails = [];
      json['judgment_details'].forEach((v) {
        judgmentDetails.add(Judgment_details.fromJson(v));
      });
    }
    status = json['status'];
    msg = json['msg'];
  }
  List<Judgment_details> judgmentDetails;
  int status;
  String msg;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    if (judgmentDetails != null) {
      map['judgment_details'] = judgmentDetails.map((v) => v.toJson()).toList();
    }
    map['status'] = status;
    map['msg'] = msg;
    return map;
  }

}

